mapper.update:
update order_info set
status = #{status},
update_time =#{updateTime},
remark_1 = #{remark1}
where order_no = #{orderNo} and status = 2

    and goods_id = #{goodsId}

mapper.select:
select order_code orderCode from order_info
 where user_id = #{userId}
 and goods_id = #{goodsId}
 and status in (2,3)
